import React, { PureComponent } from 'react';
import {
  BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

import educationData from '../../assets/censustract_education'

const data = [
  {
    name: 'Less than high school graduate', Tract: educationData.output[0].less_high_school,
  },
  {
    name: 'High school graduate', Tract: educationData.output[0].high_school,
  },
  {
    name: 'Associates degree', Tract: educationData.output[0].associate_degree,
  },
  {
    name: 'Bachelors degree or higher', Tract: educationData.output[0].bachelor,
  }
];

const getIntroOfPage = (label) => {
  if (label === 'Less than high school graduate') {
    return "Indiana State: 112,591,Monroe County: 953"

      ;
  } if (label === 'High school graduate') {
    return "Indiana State: 167,276, Monroe County: 1,923";
  } if (label === 'Associates degree') {
    return "Indiana State: 116,983, Monroe County: 2,535";
  }
  if (label === 'Bachelors degree or higher') {
    return "Indiana State: 48,819, Monroe County: 3,347";
  }
};

const CustomTooltip = ({ active, payload, label }) => {
  if (active) {
    return (
      <div className="custom-tooltip" style={{ borderRadius: "10px" }}>
        <p className="label" style={{ color: "white" }}>Education Group: {label}</p>
        <p className="label" style={{ color: "white" }}> Monroe County: {payload[0].value} %</p>
        <p className="intro" style={{ color: "white" }}>{getIntroOfPage(label)}</p>

      </div>
    );
  }

  return null;
};

export default class Example extends PureComponent {
  static jsfiddleUrl = 'https://jsfiddle.net/alidingling/30763kr7/';

  state = [
    {
      name: 'Less than high school graduate', Tract: educationData.output[0].less_high_school,
    },
    {
      name: 'High school graduate', Tract: educationData.output[0].high_school,
    },
    {
      name: 'Associates degree', Tract: educationData.output[0].associate_degree,
    },
    {
      name: 'Bachelors degree or higher', Tract: educationData.output[0].bachelor,
    }
  ]

  componentDidUpdate(nextProps) {
    // educationData

    var newData = []
    educationData.output.forEach(item => {
      if(item.census == nextProps.selectedCensus) {
        var less_high_school = {
          name: 'Less than high school graduate',
          Tract: item.less_high_school
        }

        var high_school = {
          name: 'High school graduate',
          Tract: item.high_school
        }

        var associate_degree = {
          name: 'Associates degree',
          Tract: item.associate_degree
        }

        var bachelor = {
          name: 'Bachelors degree or higher',
          Tract: item.bachelor
        }

        newData.push(less_high_school)
        newData.push(high_school)
        newData.push(associate_degree)
        newData.push(bachelor)
      }
    })

    this.state = newData;
  }

  render() {
    return (
      <div>
        {JSON.stringify(this.state)}
        <div style={{ width: '100%', height: 400 }}>
          <ResponsiveContainer>
            <BarChart
              width={500}
              height={300}
              data={this.state}
              margin={{
                top: 5, right: 30, left: 20, bottom: 5,
              }}
            >

              <CartesianGrid strokeDasharray="3 3" />

              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip wrapperStyle={{ backgroundColor: "#21262F", borderRadius: "10px" }}

                content={<CustomTooltip />} />
              <Legend />
              <Bar dataKey="Tract" fill="#8884d8" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>



    );
  }
}
