import React, { PureComponent } from 'react';
import {
    ComposedChart, Line, Area, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer,
    Legend, BarChart
} from 'recharts';
import Gender from "./genderdata"

let heightG, widthG;

const data = [
    {
        name: '<5', UnitedStates: 590, Monroe: 800, amt: 1400,
    },
    {
        name: '5-17Yrs', UnitedStates: 868, Monroe: 967, amt: 1506,
    },
    {
        name: '18-64', UnitedStates: 1397, Monroe: 1098, amt: 989,
    },
    {
        name: '65+', UnitedStates: 1480, Monroe: 1200, amt: 1228,
    },
    {
        name: 'Page E', UnitedStates: 1520, Monroe: 1108, amt: 1100,
    },
    {
        name: 'Page F', UnitedStates: 1400, Monroe: 680, amt: 1700,
    },
];

export default class Example extends PureComponent {
    componentDidMount() {

        heightG = document.getElementsByClassName('composechartdiv')[0].clientHeight
        widthG = document.getElementsByClassName('composechartdiv')[0].clientWidth
        console.log(heightG)

    }

    static jsfiddleUrl = 'https://jsfiddle.net/alidingling/94sebfL8/';

    render() {
        return (
            <div>
                <div class="row" >
                    <div class="col-md-6" >

                        <div style={{ overflow: "hidden" }} class="composechartdiv" style={{ width: "100%", height: "100%" }}>
                            <ResponsiveContainer style={{ overflow: "hidden" }} >

                                <BarChart

                                    data={data}
                                    margin={{
                                        top: 20, right: 30, left: 20, bottom: 5,
                                    }}
                                >
                                    <CartesianGrid strokeDasharray="3 3" />
                                    <XAxis dataKey="name" />
                                    <YAxis />
                                    <Tooltip />
                                    <Legend />
                                    <Bar dataKey="Monroe" stackId="a" fill="#8884d8" />
                                    <Bar dataKey="UnitedStates" stackId="a" fill="#82ca9d" />
                                </BarChart>
                            </ResponsiveContainer>

                        </div>

                    </div>
                    <div class="col-md-6">
                        <Gender></Gender>
                    </div>
                </div>
            </div>
        );
    }
}
