import React, { PureComponent } from 'react';
import {
  BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend,ResponsiveContainer
} from 'recharts';

const data = [
  {
    name: 'Page A', UnitedStates: 590, Monroe: 800, amt: 1400,
},
{
    name: 'African American', UnitedStates: 868, Monroe: 967, amt: 1506,
},
{
    name: 'American Indian', UnitedStates: 1397, Monroe: 1098, amt: 989,
},
{
    name: 'Asian', UnitedStates: 1480, Monroe: 1200, amt: 1228,
},
{
    name: 'Native Hawaian', UnitedStates: 1520, Monroe: 1108, amt: 1100,
},
{
  name: 'Hispanic Latino', UnitedStates: 1520, Monroe: 1108, amt: 1100,
},
{
  name: 'White Alone(Not Hispanic or Latino)', UnitedStates: 1520, Monroe: 1108, amt: 1100,
},
];

const getIntroOfPage = (label) => {
    if (label === 'Page A') {
      return "Page A is about men's clothing";
    } if (label === 'Page B') {
      return "Page B is about women's dress";
    } if (label === 'Page C') {
      return "Page C is about women's bag";
    } if (label === 'Page D') {
      return 'Page D is about household goods';
    } if (label === 'Page E') {
      return 'Page E is about food';
    } if (label === 'Page F') {
      return 'Page F is about baby food';
    }
  };

  const CustomTooltip = ({ active, payload, label }) => {
    if (active) {
      return (
        <div className="custom-tooltip">
          <p className="label">{`${label} : ${payload[0].value}`}</p>
          <p className="intro">{getIntroOfPage(label)}</p>
          <p className="desc">Anything you want can be displayed here.</p>
        </div>
      );
    }

    return null;
  };
export default class Example extends PureComponent {
  static jsfiddleUrl = 'https://jsfiddle.net/alidingling/30763kr7/';

  render() {
    return (
     <div>

     <div style={{ width: '100%', height: 400 }}>
        <ResponsiveContainer>
        <BarChart
        width={500}
        height={300}
        data={data}
        margin={{
          top: 5, right: 30, left: 20, bottom: 5,
        }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="name" />
        <YAxis />
        <Tooltip  wrapperStyle={{ backgroundColor: "red" }}
        content={<CustomTooltip />} />
        <Legend />
        <Bar dataKey="Monroe" fill="#8884d8" />
        <Bar dataKey="UnitedStates" fill="#82ca9d" />
      </BarChart>
          </ResponsiveContainer>
          </div>
     </div>



    );
  }
}
