import React from 'react';
import ReactDOM from 'react-dom';
import mapboxgl from 'mapbox-gl';
import './map.css';
mapboxgl.accessToken = 'pk.eyJ1IjoiaXNoa3VtYXIiLCJhIjoiY2thN3Fucm10MDZycTJwbzVsZGhkZDZkYyJ9.ZMMsGounuGq3S56MEsEFmw';
var COLORS = ['#8c510a', '#d8b365', '#f6e8c3', '#c7eae5', '#5ab4ac', '#01665e'],
            BREAKS = [-1000, -500, 0, 500, 1000, 1500],
            FILTERUSE;
class Map extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lng: -86.45,
      lat: 39.130,
      zoom: 9
    };
  }
  // mapbox://styles/ishkumar/ckaebnm1r1a5o1imrha754k6l
  // mapbox://styles/ishkumar/ckarq6o2g00vm1jpkbnp3z3ec
  componentDidMount() {
    const map = new mapboxgl.Map({
      container: this.mapContainer,
      style: 'mapbox://styles/ishkumar/ckaebnm1r1a5o1imrha754k6l',
      center: [this.state.lng, this.state.lat],
      zoom: this.state.zoom
    });
    mapboxgl.addControl(new mapboxgl.Navigation());

    map.on('load', function () {
      map.addSource('dc', {
          type: 'geojson',
          data: 'https://gist.githubusercontent.com/hrecht/82b6440ed3b982a6f594/raw/48832482382f81d1f6943896626e21b80da6b45c/data.geojson'
      });

      map.addLayer({
          "id": "tracts",
          "type": "fill",
          "source": "dc",
          "paint": {
              "fill-color": {
                  property: 'TOTPOPCHG',
                  stops: [
              [BREAKS[0], COLORS[0]],
              [BREAKS[1], COLORS[1]],
              [BREAKS[2], COLORS[2]],
              [BREAKS[3], COLORS[3]],
              [BREAKS[4], COLORS[4]],
              [BREAKS[5], COLORS[5]]]
              },
              "fill-opacity": 0.7,
              "fill-outline-color": "#ffffff"
          }
      });
      map.on("mousemove", function (e) {
        var features = map.queryRenderedFeatures(e.point, {
            layers: ["tracts"]
        });

        if (features.length) {
            //show name and value in sidebar
            document.getElementById('tooltip-name').innerHTML = "Tract " + features[0].properties.FEDTRACTNO;
            document.getElementById('tooltip').innerHTML = Math.round(features[0].properties.TOTPOPCHG);
            //for troubleshooting - show complete features info
            //document.getElementById('tooltip').innerHTML = JSON.stringify(features, null, 2);
        } else {
            //if not hovering over a feature set tooltip to empty
            document.getElementById('tooltip-name').innerHTML = "";
            document.getElementById('tooltip').innerHTML = "";
        }
    });
  });
  }

  render() {
    return (
      <div>
        <div ref={el => this.mapContainer = el} className="mapContainer" />
        <div id='sidebar'>
        <h1>Choropleth example using Mapbox GL js</h1>
        <h2>Population change, 1990-2000</h2>
        <div id="tooltip-name"></div>
        <div id='tooltip'></div>
    </div>
      </div>
    )
  }



}

export default Map;
